import React, { useState } from 'react'
import '../App.css'

interface questionProps {
    question: string,
    answer: string,
    options: string[],
    handleStep: ()=>void
}

const Quiz: React.FC<questionProps> = ({question,answer,options,handleStep}) => {
    // let [score,setScore] = useState(0)
    let setActive = (answer : string) => {
        console.log(answer)
        // if (answer===e.target.value) {
        //     setScore(++score)
        // }
        // console.log(score)
        // e.target.classList.add('active')
    }
    return (
        <div className='quiz-container'>
            <div>
                <h3> Q: {question}</h3>
            </div>
            <div>
                <ul>
                    {options.map((answer, i) => {
                        return (
                        <li key={i} value={answer} onClick={setActive(answer)} className='option-list'>{answer}</li>
                        )
                    })}
                </ul>
            </div>
            <div>
                <button className='next' onClick={handleStep}>Next</button>
            </div>
        </div>
    )
}

export default Quiz
