import React, { useState } from 'react'
import '../App.css'

interface questionProps {
    question: string,
    answer: string,
    options: string[],
    handleStep: void
}

const Quiz: React.FC<questionProps> = ({question,answer,options}) => {
    // let [answerses,setAnswer] = useState('')
    let setActive = (e : any) => {
        // setAnswer(e.target.value)
        e.target.classList.add('active')
    }

    if (!options)
        return(
            <h3>Loading...</h3>
        )


    // const answers = [10, 20, 30, 40]
    return (
        <div className='quiz-container'>
            <div>
                <h3> Q: {question}</h3>
            </div>
            <div>
                <ul>
                    {options.map((answer, i) => {
                        return (
                        <li key={i} value={answer} onClick={setActive} className='option-list'>{i+1}:{answer}</li>
                        )
                    })}
                </ul>
            </div>
            <div>
                <button className='next'>Next</button>
            </div>
        </div>
    )
}

export default Quiz
